import React, {useState,useEffect} from 'react';
import MUIDataTable from "mui-datatables";
import {MuiThemeProvider, createMuiTheme} from '@material-ui/core';
import Grid from '@material-ui/core/Grid';
import { makeStyles } from '@material-ui/core/styles';
import axios from 'axios';
import DatePicker from '../MdcMessages/GenerateReport/DatePicker';
import {ATAMainSelector,EqIDSelector} from '../MdcMessages/GenerateReport/Selectors';
import Button from '@material-ui/core/Button';
import Paper from '@material-ui/core/Paper';
import TableCell from '@material-ui/core/TableCell';
import TableRow from '@material-ui/core/TableRow';
import CorrelationSubTable from './CorrelationSubTable';

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },
    form:{
      '& .MuiTextField-root': {
        margin: theme.spacing(1),
        // width: '25ch',
        // marginBottom:20,
    },
  },
  paper: {
    // margin: 'auto',
    // width: '1200px',
    margin: '20px auto 23px 20px',
    width: '92vw',
  },
  container: {
    padding: '20px 40px',
  },
  button:{
    //margin:'40px auto',
    height: '50px',
    width:'100%',
    backgroundColor:"#C5D3E0",
  },
  Grid:{
    paddingLeft:'30px',
    margin: 'auto',
  },
  card:{
    backgroundColor: "#C5D3E0",
    textAlign: 'center',
    justify: 'center',
    padding: '5px',
  },
  formLabel:{
    fontWeight: 'bold',
    color: 'black',
    marginBottom: '20px',
  },
  TableGrid:{
    paddingLeft:'11px',
    margin: '0px',
    width:'94vw',
  }
}));

const PMTable = (props) => {
  const classes = useStyles();
  const [dateFrom, setDateFrom] = useState();
  const [dateTo, setDateTo] = useState();
  const [ATAMain, setATAMain] = useState();
  const [EqID, setEqID] = useState('');
  const [data, setData] = useState([]);
  const [responseData,setResponseData] = useState([]);

  const handleDateFrom = (date) => {
    setDateFrom(date);
    console.log(date);
  };

  const handleDateTo = (date) => {
    setDateTo(date);
    console.log(date);
  };
  const handleATAChange = (ATA) => {
    setATAMain(ATA);
    console.log(ATA);
  };
  const handleEqIDChange = (eqIDList) => {
    setEqID(eqIDList);
    console.log(eqIDList);
  };

  const handleGeneratePMTable = ()=> {
  /*http://localhost:8000/corelation/11-11-2020/11-12-2020/B1-008003/27*/

  const path = 'http://localhost:8000/corelation/' + dateFrom + '/' + dateTo + '/' + EqID + '/' + ATAMain;
  //const path = 'http://localhost:8000/corelation/'+ dateFrom + '/' + dateTo + '/B1-008003/27';
  console.log(path);
  try{
    axios.post(path).then(function (res) {
      var data = JSON.parse(res.data);
      setData(data);
    });
  } catch (err) {
    console.error(err);
  } 
  }

const columns = [
  {
    name: "p_id",
    label: "p_id",
    options: {
      filter: true,
      filterType: 'dropdown',
      sort: true,
      setCellProps: () => ({style: {whiteSpace:'nowrap'}})
    }
  },
  {
    name: 'operator', 
    label: 'Operator',
    options: {
      filter: true,
      filterType: 'dropdown',
      sort: true,
      setCellProps: () => ({style: {whiteSpace:'nowrap'}})
    }
  },
  {
    name: 'model', 
    label: 'Model',
    options: {
      filter: true,
      filterType: 'dropdown',
      sort: true,
      setCellProps: () => ({style: {whiteSpace:'nowrap', minWidth: "90px"}})
    }
  },
  {
    name: 'type', 
    label: 'Type',
    options: {
      filter: true,
      filterType: 'dropdown',
      sort: true,
      setCellProps: () => ({style: {whiteSpace:'nowrap'}})
    }
  },
  {
    name: 'serialNo', 
    label: 'Serial_No',
    options: {
      filter: true,
      filterType: 'dropdown',
      sort: true,
      setCellProps: () => ({style: {whiteSpace:'nowrap'}})
    }
  },
  {
    name: 'date', 
    label: 'Date',
    options: {
      filter: false,
      sort: true,
      setCellProps: () => ({style: {whiteSpace:'nowrap'}})
    }
  },
  {
    name: 'failureFlag', 
    label: 'Failure Flag',
    options: {
      filter: true,
      filterType: 'dropdown',
      sort: true,
      setCellProps: () => ({style: {whiteSpace:'nowrap'}})
    }
  },
  {
    name: 'maintTrans', 
    label: 'MaintTrans',
    options: {
      filter: true,
      filterType: 'dropdown',
      sort: true,
      setCellProps: () => ({style: {whiteSpace:'nowrap'}})
    }
  },
  {
    name: 'maintCanc', 
    label: 'Maintenance Cancellations',
    options: {
      filter: true,
      filterType: 'dropdown',
      sort: true,
      setCellProps: () => ({style: {whiteSpace:'nowrap'}})
    }
    },
    {
    name: 'maintDel', 
    label: 'Maintenance Delays',
    options: {
      filter: true,
      filterType: 'dropdown',
      sort: true,
      setCellProps: () => ({style: {whiteSpace:'nowrap',minWidth: "120px"}})
    }
    },
