    options: {
      filter: true,
      filterType: 'dropdown',
      sort: true,
      setCellProps: () => ({style: {whiteSpace:'nowrap'}})
    }
    },
    {
    name: 'campType', 
    label: 'Camp Type',
    options: {
      filter: true,
      filterType: 'dropdown',
      sort: true,
      setCellProps: () => ({style: {whiteSpace:'nowrap'}})
    }
    },
    {
    name: 'MRB', 
    label: 'MRB',
    options: {
      filter: true,
      filterType: 'dropdown',
      sort: true,
      setCellProps: () => ({style: {whiteSpace:'nowrap',minWidth: "100px"}})
    }
    },
    {
    name: 'discrepancy', 
    label: 'Discrepancy',
    options: {
      filter: true,
      filterType: 'dropdown',
      sort: true,
      setCellProps: () => ({style: {whiteSpace:'nowrap'}})
    }
    },
    {
    name: 'corActions', 
    label: 'Corrective Action',
    options: {
      filter: true,
      filterType: 'dropdown',
      sort: true,
      setCellProps: () => ({style: {whiteSpace:'nowrap'}})
    }
    },
    {
    name: 'totalHours', 
    label: 'AC Total Hours',
    options: {
      filter: true,
      filterType: 'dropdown',
      sort: true,
      setCellProps: () => ({style: {whiteSpace:'nowrap'}})
    }
    },
    {
    name: 'totalCycles', 
    label: 'AC Total Cycles',
    options: {
      filter: true,
      filterType: 'dropdown',
      sort: true,
      setCellProps: () => ({style: {whiteSpace:'nowrap'}})
    }
    },
    {
    name: 'squawkSource', 
    label: 'Squawk Source',
    options: {
      filter: true,
      filterType: 'dropdown',
      sort: true,
      setCellProps: () => ({style: {whiteSpace:'nowrap'}})
    }
    },
    {
    name: 'ATA', 
    label: 'ATA',
    options: {
      filter: true,
      filterType: 'dropdown',
      sort: true,
      setCellProps: () => ({style: {whiteSpace:'nowrap'}})
    }
    },
    {
    name: 'station', 
    label: 'Station',
    options: {
      filter: true,
      filterType: 'dropdown',
      sort: true,
      setCellProps: () => ({style: {whiteSpace:'nowrap'}})
    }
    },
    {
    name: 'ATA_SUB', 
    label: 'ATA SUB',
    options: {
      filter: true,
      filterType: 'dropdown',
      sort: true,
      setCellProps: () => ({style: {whiteSpace:'nowrap'}})
    }
    },
    {
    name: 'ATA_Main', 
    label: 'ATA Main',
    options: {
      filter: true,
      filterType: 'dropdown',
      sort: true,
      setCellProps: () => ({style: {whiteSpace:'nowrap'}})
    }
    },
];

// useEffect( () => {
  
// }, []);

if (data){
  data.map((item => {
    responseData.push(
      {
        p_id: item["p_id"], 
        operator: item["Operator"], 
        model: item["Model"], 
        type: item["Type"],  
        serialNo: item["Serial_No"],  
        date: item["date"],  
        failureFlag: item["Failure Flag"],  
        maintTrans: item["Maint Trans"],   
        maintCanc: item["Maintenance Cancellations"],   
        maintDel: item["Maintenance Delays"],  
        inspection: item["Inspection"],  
        campType: item["Camp Type"],   
        MRB: item["MRB"],   
        discrepancy: item["Discrepancy"],  
        corActions: item["Corrective Action"], 
        totalHours: item["AC Total Hours"],  
        totalCycles: item["AC Total Cycles"],  
        squawkSource:  item["Squawk Source"],
        ATA: item["ATA"],
        station: item["Station"],
        ATA_SUB: item["ATA_SUB"],
        ATA_Main: item["ATA_Main"],
      }
    );
     return responseData
  }
  ));

const options = {
  filter: true,
  filterType: 'multiselect',
  responsive: "standard",
  fixedHeader: true,
  expandableRows: true,
  renderExpandableRow: (rowData, rowMeta) => {
    console.log(rowData, rowMeta);
    return (
    //   <TableRow>
    //   <TableCell />
    //   <TableCell colSpan={2}>
    //     <CorrelationSubTable
    //       responseData = {responseData}
    //       p_id = {rowData[0]}
    //       dateFrom = {dateFrom}
    //       dateTo = {dateTo}
    //     />
    //    </TableCell>
    // </TableRow>
    <TableRow>
        <TableCell colSpan={rowData.length+1}>
        <CorrelationSubTable
      responseData = {responseData}
      p_id = {rowData[0]}
      dateFrom = {dateFrom}
      dateTo = {dateTo}
      EqID = {EqID}
      ATAMain = {ATAMain}
    />
        </TableCell>
        </TableRow>
    
    );
  },
  fixedSelectColumn: true,
  downloadOptions: {
    filename: 'Correlation Report from ' + dateFrom + ' to ' + dateTo + '.csv',
    separator: ',',
  },
  draggableColumns: {
    enabled: false,
    transitionTime: 300,
  },
  elevation: 4,
  rowsPerPage: 20,
  rowsPerPageOptions: [20, 50],
  selectToolbarPlacement:"none",
};

const theme = createMuiTheme({
  palette: {type: 'light'},
  typography: {useNextVariants: true},
});

  return (
    <div className={classes.root}>
    <Paper className={classes.paper}>
        <div className ={classes.card}>
          <h2>Correlation Filters</h2>
        </div>
        <div className={classes.container}>
        <Grid className={classes.Grid} container spacing={3}> 
          <Grid item xs={3}>     
            <ATAMainSelector 
              handleATAChange = {handleATAChange}
            />              
          </Grid>     
          <Grid item xs={3}>     
            <EqIDSelector 
              handleEqIDChange = {handleEqIDChange}
            />                   
          </Grid>    
          <Grid item xs={2}>      
            <DatePicker 
              label = "From"
              handleDateFrom = {handleDateFrom}
            />   
          </Grid>
          <Grid item xs={2}>         
            <DatePicker 
              label = "To"
              handleDateTo = {handleDateTo}
            />   
          </Grid>
          <Grid item xs={2}>  
            <Button 
              variant="contained" 
              onClick = {async()=>handleGeneratePMTable()}
              className={classes.button}>
                Generate Correlation Table
            </Button>  
          </Grid>     
          </Grid>      
          </div>
      </Paper>
        <Grid className={classes.TableGrid} container spacing={3}> 
          <Grid item xs={12}>
            <MuiThemeProvider theme={theme}>
              <MUIDataTable
                title="PM Report"
                data={responseData}
                columns={columns}
                options={options}
              />
            </MuiThemeProvider> 
          </Grid> 
        </Grid> 
  </div>
  );
  }
}

export default PMTable;
