import React, {useState} from 'react';
import MUIDataTable from "mui-datatables";
import {MuiThemeProvider, createMuiTheme} from '@material-ui/core';
import Grid from '@material-ui/core/Grid';
import { makeStyles } from '@material-ui/core/styles';
//Buttons Imports
import Button from '@material-ui/core/Button';
import { useHistory } from "react-router-dom";
//Axios Imports 
import axios from 'axios';


const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
    alignItems:"center",
    maxWidth: '92vw',
    margin:  '20px',
  },
  button:{
    margin:'0px 30px 15px 0px',
    backgroundColor:"#C5D3E0",
    width: '100%',
    // height: '51px',
  },
}));

const HistoryReport = (props) => {
  const [ACSNList, setACSNList] = useState([]);
  const [eqList, setEqList] = useState([]);
  const [rowsSelectedState, setRowsSelected] = useState([]);
  const [flagConditions,setFlagConditions] = useState('');
  const [flagData,setFlagData] = useState('');

  const HandleMultipleRowSelect = (rowsSelectedData, allRows, rowsSelected) => {
    //Data index to get the row no matter the sorting or filtering;
    setRowsSelected(rowsSelected);
    let FlagACArray = [];
    let FlagB1Array = [];
    Object(rowsSelected).map((item => {
      FlagACArray.push(data[item].ACSN);
      FlagB1Array.push(data[item].B1Equation);
      return FlagACArray;
    }));
    setACSNList(FlagACArray);
    setEqList(FlagB1Array);
  };

// ----- States and handle Functions for Buttons -----
const history = useHistory();
  const handleGenerateFlagReport = (event) => {
    setFlagConditions(
      {
        analysis: props.reportConditions.analysis,
        occurences: props.reportConditions.occurences,
        legs: props.reportConditions.legs,
        HistExEqID: props.reportConditions.EqID,
        intermittent: props.reportConditions.intermittent,
        days: props.reportConditions.days,
        operator: props.reportConditions.airline,
        HistAta: props.reportConditions.ATAMain,
        messages: props.reportConditions.messagesChoice,
        fromDate: props.reportConditions.fromDate,
        toDate: props.reportConditions.toDate,
        FlagEqID: "('"+ eqList.join("','") +"')",
        FlagACSN:"('"+ ACSNList.join("','") +"')"
      },
    );
    let flag = true;
    Object.values(flagConditions).map(item => {
      if (item === ""){
        flag = false;
      }
      return flag;
    });
  
    if (flag === true) {  
      console.log(flagConditions);
      ///MDCRawData/{ATAMain_list}/{exclude_EqID_list}/{fromDate}/{toDate}"
      //http://localhost:8000/MDCRawData/('32','22')/('B1-007553','B1-246748')/2020-11-05/2020-11-12
      
      const path = '';
      /*const path = 'http://localhost:8000/MDCRawData/' + rawDataConditions.ata + '/' + rawDataConditions.eqID + '/' + rawDataConditions.operator + 
      '/' + rawDataConditions.ata + '/' + rawDataConditions.messages + '/' + rawDataConditions.fromDate + '/' + rawDataConditions.toDate;
      */

      try{
        axios.post(path).then(function (res) {
          // console.log(res);
          var data = JSON.parse(res.data);
          setFlagData(data);
        });
      } catch (err) {
        console.error(err);
      }
    }
      history.push({
        pathname: '/flag',
        state: {
          flagConditions: flagConditions,
          flagData: flagData
        }
      });
    };

  const columns = [
    {
      name: 'ACSN', 
      label: 'ACSN',
      options: {
       filter: true,
       filterType: 'dropdown',
       sort: true,
       setCellProps: () => ({style: {whiteSpace:'nowrap'}})
      }
    },
    {
      name: 'EICASMessages', 
      label: 'EICAS Messages',
      options: {
       filter: true,
       filterType: 'dropdown',
       sort: true,
       setCellProps: () => ({style: {whiteSpace:'nowrap', minWidth: "90px"}})
      }
    },
    {
      name: 'mdcMessages', 
      label: 'MDC Messages',
      options: {
       filter: true,
       filterType: 'dropdown',
       sort: true,
       setCellProps: () => ({style: {whiteSpace:'nowrap'}})
      }
    },
    {
      name: 'LRU', 
      label: 'LRU',
      options: {
       filter: true,
       filterType: 'dropdown',
       sort: true,
       setCellProps: () => ({style: {whiteSpace:'nowrap'}})
      }
    },
    {
      name: 'ATA', 
      label: 'ATA',
      options: {
       filter: false,
       sort: true,
       setCellProps: () => ({style: {whiteSpace:'nowrap'}})
      }
    },
    {
      name: 'B1Equation', 
      label: 'B1 Equation',
      options: {
       filter: true,
       filterType: 'dropdown',
       sort: true,
       setCellProps: () => ({style: {whiteSpace:'nowrap'}})
      }
    },
    {
      name: 'type', 
      label: 'Type',
      options: {
       filter: true,
       filterType: 'dropdown',
       sort: true,
       setCellProps: () => ({style: {whiteSpace:'nowrap'}})
      }
    },
    {
      name: 'equationDescription', 
      label: 'Equation Description',
      options: {
       filter: true,
       filterType: 'dropdown',
       sort: true,
       setCellProps: () => ({style: {whiteSpace:'nowrap'}})
      }
     },
     {
      name: 'totalOccurences', 
      label: 'Total Occurences',
      options: {
       filter: true,
       filterType: 'dropdown',
       sort: true,
       setCellProps: () => ({style: {whiteSpace:'nowrap',minWidth: "120px"}})
      }
     },
     {
      name: 'consecutiveDays', 
      label: 'Consecutive Days',
      options: {
       filter: true,
       filterType: 'dropdown',
       sort: true,
       setCellProps: () => ({style: {whiteSpace:'nowrap',minWidth: "120px"}})
      }
     },
     {
      name: 'ConsecutiveFlights', 
      label: 'Consecutive Flights',
      options: {
       filter: true,
       filterType: 'dropdown',
       sort: true,
       setCellProps: () => ({style: {whiteSpace:'nowrap'}})
      }
     },
     {
      name: 'intermittent', 
      label: 'Intermittent',
      options: {
       filter: true,
       filterType: 'dropdown',
       sort: true,
       setCellProps: () => ({style: {whiteSpace:'nowrap'}})
      }
     },
     {
      name: 'reasons', 
      label: 'Reasons for flag',
      options: {
       filter: true,
       filterType: 'dropdown',
       sort: true,
       setCellProps: () => ({style: {whiteSpace:'nowrap'}})
      }
     },
     {
      name: 'priority', 
      label: 'Priority',
      options: {
       filter: true,
       filterType: 'dropdown',
       sort: true,
       setCellProps: () => ({style: {whiteSpace:'nowrap',minWidth: "100px"}})
      }
     },
     {
      name: 'topMessage', 
      label: 'Known Top Message - Recommended Documents	MHIRJ ISE',
      options: {
       filter: true,
       filterType: 'dropdown',
       sort: true,
       setCellProps: () => ({style: {whiteSpace:'nowrap'}})
      }
     },
     {
      name: 'recommendation', 
      label: 'MHIRJ ISE Recommendation',
      options: {
       filter: true,
       filterType: 'dropdown',
       sort: true,
       setCellProps: () => ({style: {whiteSpace:'nowrap'}})
      }
     },
     {
      name: 'comments', 
      label: 'Additional Comments',
      options: {
       filter: true,
       filterType: 'dropdown',
       sort: true,
       setCellProps: () => ({style: {whiteSpace:'nowrap'}})
      }
     },
     {
      name: 'input', 
      label: 'MHIRJ ISE Input',
      options: {
       filter: true,
       filterType: 'dropdown',
       sort: true,
       setCellProps: () => ({style: {whiteSpace:'nowrap'}})
      }
     },
    ];

    let data = [];
    if (props.data.map != null || props.data.map!= undefined ){
      props.data.map((item => {
        //console.log(item["AC SN"]); 
        data.push(
          {
            ACSN: item["AC SN"], 
            EICASMessages: item["EICAS Message"], 
            mdcMessages: item["MDC Message"],  
            LRU: item["LRU"],  
            ATA: item["ATA"],  
            B1Equation: item["B1-Equation"],  
            type: item["Type"],   
            equationDescription: item["Equation Description"],   
            totalOccurences: item["Total Occurences"],  
            consecutiveDays: item["Consective Days"],
            ConsecutiveFlights: item["Consecutive FL"],  
            intermittent: item["Intermittent"],  
            reasons: item["Reason(s) for flag"],   
            priority: item["Priority"],   
            topMessage: item["Known Top Message - Recommended Documents"],  
            recommendation: item["MHIRJ ISE Recommendation"], 
            comments: item["Additional Comments"],  
            input: item["MHIRJ ISE Input"],  
          }
        );
        return data;
      }
      ));
    }

    const options = {
      selectableRows: 'multiple',
      selectableRowsOnClick: true,
      rowsSelected: rowsSelectedState,
      onRowSelectionChange: HandleMultipleRowSelect,
      filter: true,
      filterType: 'multiselect',
      responsive: "standard",
      fixedHeader: true,
      fixedSelectColumn: true,
      // rowHover: true,
      //tableBodyMaxHeight: '700px',
      //enableNestedDataAccess: true,
      downloadOptions: {
        filename: 'MdcRawData.csv',
        separator: ',',
      },
      draggableColumns: {
        enabled: false,
        transitionTime: 300,
      },
      elevation: 4,
      rowsPerPage: 25,
      rowsPerPageOptions: [25,50],
      selectToolbarPlacement:"none",
      // setFilterChipProps: (colIndex, colName, data) => {
      //   return {
      //     color: 'primary',
      //     variant: 'outlined',
      //     className: 'testClass123',
      //   };
      // }
    };

    const theme = createMuiTheme({
      palette: {type: 'light'},
      typography: {useNextVariants: true},
  });
  
const classes = useStyles();
  return (
    <div className={classes.root}>
      <Grid container spacing={0}>
        <Grid item xs={10}></Grid>
        <Grid item xs={2}>
        <Button 
          variant="contained" 
          onClick = {()=>handleGenerateFlagReport()}
          className={classes.button}>
            Generate Flag Report
        </Button>
        </Grid>
        <Grid item xs={12}>
            <MuiThemeProvider theme={theme}>
              <MUIDataTable
                title= {props.title}
                data={data}
                columns={columns}
                options={options}
              />
            </MuiThemeProvider> 
        </Grid> 
      </Grid> 
    </div>
  );
}
export default HistoryReport;
